import java.util.Map.Entry;
import java.util.*;
import java.io.*;	
import org.dom4j.*;		
import org.dom4j.io.SAXReader;		
import groovy.json.*


// Method to read properties type configurations and set them in SoapUI project for test case execution

def propertiesConfigSetter(def target, def configFilePath)
{

target=target.trim().toUpperCase();
def project=testRunner.testCase.testSuite.project;

def isTargetMatched=false;
try{

Properties config=new Properties();
FileInputStream fis=new FileInputStream(configFilePath)
config.load(fis)

for(Entry<String, String> entry: config.entrySet()){
	tempKey=entry.getKey().toUpperCase();
	if(tempKey.endsWith(target)){
		project.setPropertyValue(tempKey.substring(0, tempKey.lastIndexOf("_"+target)), entry.getValue())
		isTargetMatched=true;
	}	
}
if(isTargetMatched==true){
log.info "Configurations successfully set for Target="+target+" from config file: "+configFilePath
} else{
	log.info "No matching configurations found for the provided Target="+target+" in config file: "+configFilePath+", Please check your target input & config file."
}
}
catch(Exception ex){
	log.error "Failed to set configurations for Target="+target+" from config file: "+configFilePath+". Exception: "+ex.toString()
}
} 


def searchAndGetExactXmlTag(String exactXmlData, String searchText) {
	String exactTag=null;
	String lowerCaseXmlData=exactXmlData.toLowerCase();
	String searchTerm="<"+searchText.toLowerCase()+">";	
	exactTag = exactXmlData.substring(lowerCaseXmlData.indexOf(searchTerm)+1, lowerCaseXmlData.indexOf(searchTerm)+searchTerm.length()-1);
	return exactTag;		
}


// Method to read XML type configurations and set them in SoapUI project for test case execution

def xmlConfigSetter(def target, def configFilePath){

def isTargetMatched=false;
def project=testRunner.testCase.testSuite.project;

try{
File inputFile = new File(configFilePath);	
BufferedReader br=new BufferedReader(new FileReader(inputFile));
String temp="";
String data="";
while((temp=br.readLine())!=null) {
	data=data+temp;
}
br.close();  

StringReader sr=new StringReader(data);
SAXReader saxReader = new SAXReader();						
Document document = saxReader.read(sr);	 
String exactTag=searchAndGetExactXmlTag(data, target);

List<Node> nodes=document.selectNodes("//"+exactTag+"/child::*");

if(!nodes.isEmpty()){
	isTargetMatched=true;
	for(Node node:nodes) {
		project.setPropertyValue(node.getName(), node.getText())
	}	
}

if(isTargetMatched==true){
log.info "Configurations successfully set for Target="+target+" from config file: "+configFilePath
} else{
	log.info "No matching configurations found for the provided Target="+target+" in config file: "+configFilePath+", Please check your target input & config file."
}

}catch(Exception ex){
	log.error "Failed to set configurations for Target="+target+" from config file: "+configFilePath+". Exception: "+ex.toString()
}

}


// Method to read JSON type configurations and set them in SoapUI project for test case execution

def jsonConfigSetter(def target, def configFilePath){

def isTargetMatched=false;
def project=testRunner.testCase.testSuite.project;

try{

// all json parser code here
File inputFile = new File(configFilePath);	
BufferedReader br=new BufferedReader(new FileReader(inputFile));
String temp="";
String jsonData="";
while((temp=br.readLine())!=null) {
	jsonData=jsonData+temp;
}
br.close(); 

def jsonSlurper = new JsonSlurper()
def jsonObject = jsonSlurper.parseText(jsonData)

def envSettings= jsonObject.EnvironmentSettings

def requiredEnvMap=null

OUTER_LOOP: for(envSetting in envSettings){

				for(envs in envSetting){

					if(target.equalsIgnoreCase(envs.key)){

						isTargetMatched=true;
						requiredEnvMap=envs;
						break OUTER_LOOP;
						}
				}
			}	


if(requiredEnvMap!=null){
def exactEnvMap=requiredEnvMap.getValue()
for(entry in exactEnvMap){
	project.setPropertyValue(entry.key, entry.value)
}
}


if(isTargetMatched==true){
log.info "Configurations successfully set for Target="+target+" from config file: "+configFilePath
} else{
	log.info "No matching configurations found for the provided Target="+target+" in config file: "+configFilePath+", Please check your target input & config file."
}


}catch(Exception ex){
	log.error "Failed to set configurations for Target="+target+" from config file: "+configFilePath+". Exception: "+ex.toString()
}

}


// caller code

// method to set the environment configurations in SoapUI project file based on type of Config file provided e.g. - .Config, .Propertiues, .xml, .json

def project=testRunner.testCase.testSuite.project;
def target = project.getPropertyValue("Target")
def configFilePath=project.getPropertyValue("ConfigFilePath")


if(configFilePath.length()==0 || target.length()==0){
	log.info "Target or ConfigFilePath not provided. Hence the execution will happen based on default value existing inside project file!!"
} else{

def configFileExtension = configFilePath.substring(configFilePath.lastIndexOf("."))

log.info "Config file extension type = "+configFileExtension

configFileExtension=configFileExtension.toLowerCase()


switch(configFileExtension){

	case ".config":
	case ".properties":
	case ".prop":
	propertiesConfigSetter(target, configFilePath);
	break;

	case ".xml":
	xmlConfigSetter(target, configFilePath);
	break;

	case ".json":
	jsonConfigSetter(target, configFilePath);
	break;

	default: 
	lof.info "Provided config file extension isn't supported. Please provided any of the following type of config file - .properties, .config, .json or .xml"

}

}

