// Custom Data Structure to store Skipped Test Case Related Details
class SkippedTestCase{

	def serialNumber, testSuiteName, testCaseName;

	SkippedTestCase(serialNumber, testSuiteName, testCaseName){
		this.serialNumber=serialNumber;
		this.testSuiteName=testSuiteName;
		this.testCaseName=testCaseName;
	}

	String toString(){
		return serialNumber + ". " + testSuiteName + " -> " + testCaseName;
	}
}

// Custom Data Structure to store Skipped Test Suite Related Details

class SkippedTestSuite{

	def serialNumber, testSuiteName;
	SkippedTestSuite(serialNumber,testSuiteName){
		this.serialNumber=serialNumber;
		this.testSuiteName=testSuiteName;
	}

	String toString(){
		return serialNumber + ". " + testSuiteName;
	}
}


// Method to find out the difference between two date objects and return the difference in milliseconds

public static long getDateDiffInMilliseconds(Date date1, Date date2) {
	TimeUnit timeUnit=TimeUnit.MILLISECONDS;
    	long diffInMillies = date2.getTime() - date1.getTime();
    	return timeUnit.convert(diffInMillies,timeUnit);
}

// Method to calculate total execution time for the entire suite in milliseconds and format it in proper string to be displayed in report

public  static String calculateTotalExecDurationFromMillis(long millis) {
String formmatedTime="";
long ms=millis%1000;
formmatedTime=String.format("%02d:%02d:%02d", 
TimeUnit.MILLISECONDS.toHours(millis),
TimeUnit.MILLISECONDS.toMinutes(millis) -  
TimeUnit.HOURS.toMinutes(TimeUnit.MILLISECONDS.toHours(millis)),
TimeUnit.MILLISECONDS.toSeconds(millis) - 
TimeUnit.MINUTES.toSeconds(TimeUnit.MILLISECONDS.toMinutes(millis)));   

formmatedTime=formmatedTime+"."+String.format("%03d",ms);

}

// Encrypt/Hide CreateToken sensitive info
def encryptOrHideCreateTokenResponse()
{

	def responseOfCreateToken = testRunner.testCase.testSteps["CreateToken"].testRequest.response.responseContent
	def jsonSlurper = new JsonSlurper()
	def jsonObject = jsonSlurper.parseText(responseOfCreateToken)
	jsonObject.access_token="**********"
	jsonObject.refresh_token="**********"
	jsonObject.id_token="**********"

	return jsonObject.toString()
}


// Custom class data structure to hold test step related data

class StepDetails{	

public String stepStatus=""
public String hrefLinkToRespFile=""
public String respTimeMs=""
public String httpStatusCode=""
public StepDetails(String stepStatus, String hrefLinkToRespFile, String respTimeMs, String httpStatusCode){
	this.stepStatus=stepStatus
	this.hrefLinkToRespFile=hrefLinkToRespFile
	this.respTimeMs=respTimeMs
	this.httpStatusCode=httpStatusCode
}
}

// Method to get the endpoint hit during any api call at runtime

def getRuntimeEndpoint(def compileTimeEndpoint){

def runtimeEndpoint=compileTimeEndpoint
try{
runtimeEndpoint=runtimeEndpoint.replace("\$","")	
if(runtimeEndpoint.contains("{#Project")){	
	runtimeEndpoint=runtimeEndpoint.replace("{#Project#","")
	runtimeEndpoint=runtimeEndpoint.substring(0,runtimeEndpoint.indexOf("}"))	
	runtimeEndpoint=testRunner.testCase.testSuite.project.getPropertyValue(runtimeEndpoint)	
}
} catch(Exception ex){
	log.error "Error occurred in the method - getRuntimeEndpoint. Exception: "+ex.toString()
}

return runtimeEndpoint
}


// Methlod to get runtime header value of any test step, if the test step is parameterized

def getRuntimeHeaderValue(def compileTimeHeaderValue){

	def runtimeHeaderValue=""

	try{
	compileTimeHeaderValue=compileTimeHeaderValue.substring(1,compileTimeHeaderValue.length()-1)
	compileTimeHeaderValue=compileTimeHeaderValue.replace("\$","&amp;")

	def arrCompTimeHeaderParts=compileTimeHeaderValue.split("&amp;")

	for(int i=0;i<arrCompTimeHeaderParts.length;i++){		
		def temp=arrCompTimeHeaderParts[i];		
		if(!temp.contains("{#")){
			continue;
		}
		def propValue=""
		def propName=""

		if(temp.contains("{#Project")){	
			propName=temp.replace("{#Project#","")
			propName=propName.substring(0,propName.indexOf("}"))	
			propValue=testRunner.testCase.testSuite.project.getPropertyValue(propName)		
		} else if(temp.contains("{#TestSuite")){
			propName=temp.replace("{#TestSuite#","")
			propName=propName.substring(0,propName.indexOf("}"))	
			propValue=testRunner.testCase.testSuite.getPropertyValue(propName)	
		} else if(temp.contains("{#TestCase")){
			propName=temp.replace("{#TestCase#","")
			propName=propName.substring(0,propName.indexOf("}"))	
			propValue=testRunner.testCase.getPropertyValue(propName)	
		}
		arrCompTimeHeaderParts[i]=propValue+"\r\n"

	}


	for(str in arrCompTimeHeaderParts){
		str=str.replace("&amp;","")
		runtimeHeaderValue=runtimeHeaderValue+str
	}
	} catch(Exception ex){
		log.error "Error occurred in the method - getRuntimeHeaderValue. Exception: "+ex.toString()
	}

	return runtimeHeaderValue;
}

// Method to write/store the execution logs in txt format 

def writeLogToFile(def logFilePath){

Thread.sleep(3000)
try{
def logArea=com.eviware.soapui.SoapUI.logMonitor.getLogArea("script log")
def logFile = new File(logFilePath)

if(logArea !=null){
def model = logArea.model
if(model.size > 0) {
	for(c in 0..(model.size-1)){
	logFile.append(model.getElementAt(c).toString() + "\r\n")
	}
}
}

log.info "Execution log file written to path: "+logFilePath
} catch(Exception ex){
	log.error "Error occurred while trying to write the execution log file to path: "+logFilePath+", Execption: "+ex.toString()
}


}

// Method to write data to file

def writeDataToFile(def data, def filePath){
BufferedWriter bw = null;
try
{
File file = new File(filePath);
file.createNewFile();
FileWriter fw = new FileWriter(file);
bw = new BufferedWriter(fw);
bw.write(data);

log.info "Data written to file: "+filePath
}catch(IOException ex){
log.error "Exception occurred while trying to write data to the provided path: "+filePath+".!! Check for access permissions. Exception:: "+ex.toString()
}
finally{
		try{
	      if(bw!=null)
		 bw.close();
	   }catch(Exception ex){
	       log.error("Error in closing the BufferedWriter"+ex);
	    }		
}
}

// method to filter out test suite object at run time by ignoring case, based on the given test suite names. Suite name would be split by comma
def filterTestSuiteObjectsIgnoreCase(def suitesToExecute){

def project=testRunner.testCase.testSuite.project
def tsList =[]; 

def allSuiteList = project.getTestSuiteList();

log.info "Suite Names Provided for Execution: ["+suitesToExecute+"]"

testSuiteList = suitesToExecute.split(",");

// removing leading/trailing spaces from suite names
for(int i=0;i<testSuiteList.length;i++) {
	testSuiteList[i]=testSuiteList[i].trim();
}


for(suiteName in testSuiteList){
	def matchFound=false;
	for(testSuite in allSuiteList){
		if(testSuite.getName().equalsIgnoreCase(suiteName)){
			tsList.add(testSuite);
			matchFound=true;
			break;
	     } 		
	}	

	if(matchFound==false){
		log.info "No test suite by name: ["+suiteName +"] was found in the project! Check the given test suite name."
	}
}	


log.info "Total: ["+tsList.size()+"] matching test suites found in the project as per the given suite names"

return tsList;

}


log.info "======AUTOMATION EXECUTION STARTED======"
