def tcExecutionTableBody=""
def tsCount=0  
def tcCount=0
def passedCount=0
def failedCount=0
def stepAssertionResults=""
def skippedTestSuiteCount=0, skippedTestCaseCount=0;
def skippedTestSuiteList=[], skippedTestCaseList=[];
def totalTCRunCount=0, totalTSRunCount=0;
def tcSerialNumber=0;


// looping through all the test suites except the code controller/driver
def currentExecutingTSName=testRunner.testCase.testSuite.getName()
def startTimeTotal = Calendar.getInstance().getTime();


// New code added for selective test suite execution from config file -  SuitesToExecute tag value
def showBuildStatus=project.getPropertyValue("ShowBuildStatus")
def suitesToExecute=project.getPropertyValue("SuitesToExecute")

def tsList =[];

if( suitesToExecute.equalsIgnoreCase("All") || (suitesToExecute==null) || (suitesToExecute.equals("")) ){
	tsList=project.getTestSuiteList() 
} else{
	tsList = filterTestSuiteObjectsIgnoreCase(suitesToExecute)				
}




PARENT_LOOP: for(ts in tsList)
{

def tsName=ts.getName();	
if(tsName.equalsIgnoreCase(currentExecutingTSName)){
	continue;
}

tsCount++;

if(ts.isDisabled()){

skippedTestSuiteCount++
skippedTestSuiteList.add(new SkippedTestSuite(skippedTestSuiteCount, tsName));

// get the number of test cases in the disabled test suite and add it to the total available test cases across the project - tcCount

for(temp in ts.getTestCaseList()){
tcCount++;
skippedTestCaseCount++
skippedTestCaseList.add(new SkippedTestCase(skippedTestCaseCount, tsName, temp.getName()));
}



log.info "####---------SKIPPED Test Suite: ["+tsName+"]-----------#### as it is DISABLED..!!"
continue;
}


totalTSRunCount++;
log.info "********* Running Test Suite: "+tsName+" ***********"

// create a directory for each test suite
def tsDirectory = new File(htmlReportCurrentDirPath.toString()+"//"+tsName)
if (!tsDirectory.exists()) {
  tsDirectory.mkdir()
}


for(tc in ts.getTestCaseList())
{
def tcName=tc.getName()

tcCount++;

if(tc.isDisabled()){

skippedTestCaseCount++
skippedTestCaseList.add(new SkippedTestCase(skippedTestCaseCount, tsName, tcName));

log.info "####----------SKIPPED Test Case: ["+tcName+"]-----------### as it is DISABLED..!!"
continue;
}

totalTCRunCount++;
log.info "-------Running Test Case: "+tcName+"-------"

// create a directory for each test case
def tcDirectory = new File(htmlReportCurrentDirPath.toString()+"//"+tsName+"//"+tcName)
if (!tcDirectory.exists()) {
  tcDirectory.mkdir()
}
def tcStatus=""
def stepsMap=new LinkedHashMap<String,StepDetails>()

def allStepsWithStatusAndHref=""
def status=""
for(step in tc.getTestStepList())
{

def respTimeMs=""

def stepName=step.getName()

log.info "-------------Running Test Step: "+stepName+"------------"

if(step.isDisabled()){
	log.info "####----------SKIPPED Test Step: ["+stepName+"]-----------### as it is DISABLED..!!"
	continue;
}


def groovyUtils = new GroovyUtils(context)
testRunner = new WsdlTestCaseRunner(tc, null)
testStepContext = new WsdlTestRunContext(step)

def startTime = Calendar.getInstance().getTime();


// running the test step using WsdlTestCaseRunner & Context
def testStepResult=step.run(testRunner, testStepContext)

def endTime = Calendar.getInstance().getTime();
respTimeMs = getDateDiffInMilliseconds(startTime, endTime).toString()
log.info "Step ["+stepName+"] Response Time (Milliseconds): " + respTimeMs

status=testStepResult.status.toString().equalsIgnoreCase("OK")?"PASSED":testStepResult.status.toString()
def response=""

if(!(step instanceof PropertyTransfersTestStep)){

	try{
		response= step.testRequest.response.responseContent

		if(stepName.equalsIgnoreCase("CreateToken")){
		response=encryptOrHideCreateTokenResponse();
		}
		response = JsonOutput.prettyPrint(response)
	} catch(Exception ex){	
	}


// getting all assertions and their results for the current test step
stepAssertionResults="\r\n\r\nAssertion Results:\r\n=====================================================\r\n"
for(assertion in step.assertionList){
stepAssertionResults=stepAssertionResults+assertion.label+" = "+assertion.status+"\r\n"
}
log.info "Step ["+stepName+"] Assertion Results: "+stepAssertionResults


log.info "Test Step ["+stepName+"] Response="+response


def httpResponseHeaders = step.testRequest.response.responseHeaders
