def requestParamsStr=null;
for (entry in requestParamsMap) {
	if(requestParamsStr==null){
		requestParamsStr=""
	}
	def paramObj=requestParamsMap[entry.key]
     requestParamsStr=requestParamsStr+entry.key+"="+paramObj.value+"\r\n"
}

log.info "Request Params: "+requestParamsStr

// getting Request Headers
def requestHeadersMap=step.getHttpRequest().getRequestHeaders()
def requestHeadersStr=null
for (entry in requestHeadersMap) {
	if(requestHeadersStr==null){
		requestHeadersStr=""
	}
	def headerObj=requestHeadersMap[entry.key]	
	def headerValueCompileTime=headerObj.value.toString()
	def headerValueRunTime=getRuntimeHeaderValue(headerValueCompileTime);

	// new code added to hide access_token in header
	if(entry.key.equalsIgnoreCase("Authorization")){

		requestHeadersStr=requestHeadersStr+entry.key+"="+"*******"+"\r\n"
	} else{
		 requestHeadersStr=requestHeadersStr+entry.key+"="+headerValueRunTime+"\r\n"

	}




}

log.info "Request Headers: "+requestHeadersStr

// getting Request Contents
def requestBody=step.getHttpRequest().getRequestContent()
if(requestBody==null || requestBody.equals("")){
log.info "Request Body: null"	
} 


// write here code to gather the request, response, assertion status and step status in string to be written to stepFilePath
