stepReqResData=stepReqResData+"Request Headers: "+requestHeadersStr+"\r\n\r\n"	
} else{
stepReqResData=stepReqResData+"Request Headers:\r\n=====================================================\r\n"+requestHeadersStr+"\r\n\r\n"	
}

if(requestBody==null || requestBody.equals("")){
stepReqResData=stepReqResData+"Request Body: null\r\n\r\n"	
} else{
stepReqResData=stepReqResData+"Request Body:\r\n=====================================================\r\n"+requestBody+"\r\n\r\n"	
}

stepReqResData=stepReqResData+"\r\nResponse:\r\n=====================================================\r\n"+response

stepReqResData=stepReqResData+stepAssertionResults

// create a txt file for each test step to store the request, response and assertion status
def stepFilePath = htmlReportCurrentDirPath.toString()+"//"+tsName+"//"+tcName+"//"+stepName+".txt";

// stepFilePath Relative address
def stepFilePathRelative= ".//"+tsName+"//"+tcName+"//"+stepName+".txt"

writeDataToFile(stepReqResData, stepFilePath)
stepsMap.put(stepName, new StepDetails(status, stepFilePathRelative, respTimeMs, httpStatusCode));


}  // if ends here
else{	
stepsMap.put(stepName, new StepDetails(status, "", null, null))	
}


}  // step loop closing


// all steps of a test case with status & href to resp file
def stepCount=0
def tcStatusBinary=true
allStepsWithStatusAndHref=allStepsWithStatusAndHref+"<ol type='1'>"
for (entry in stepsMap) {
	def stepDetailsObj=stepsMap[entry.key]	

	if(stepDetailsObj.hrefLinkToRespFile==null || stepDetailsObj.hrefLinkToRespFile.equals("")){
		allStepsWithStatusAndHref=allStepsWithStatusAndHref+"<li>"+entry.key+" = "+stepDetailsObj.stepStatus+"</li>"    
	} else{
	allStepsWithStatusAndHref=allStepsWithStatusAndHref+"<li><a href='"+stepDetailsObj.hrefLinkToRespFile+"'>"+entry.key+" = "+stepDetailsObj.stepStatus+" [Response Time(Ms): "+stepDetailsObj.respTimeMs+", Response Code: "+stepDetailsObj.httpStatusCode+"]</a></li>"    	
	}

	if(stepDetailsObj.stepStatus.equalsIgnoreCase("PASSED")){
		tcStatusBinary=tcStatusBinary && true
	} else{
		tcStatusBinary=tcStatusBinary && false
	}	

}

tcStatus=(tcStatusBinary==true)?"PASSED":"FAILED"

allStepsWithStatusAndHref=allStepsWithStatusAndHref+"</ol>"
tcSerialNumber++;
def tcRowData="<tr><td>"+tcSerialNumber+"</td><td>"+tsName+"</td><td>"+tcName+"</td><td>"+allStepsWithStatusAndHref+"</td>"
if(tcStatus.equalsIgnoreCase("PASSED")){
	passedCount++;
	tcRowData=tcRowData+"<td class='passStatus'>"+tcStatus+"</td></tr>"
} else{
	failedCount++;
	tcRowData=tcRowData+"<td class='failStatus'>"+tcStatus+"</td></tr>"
}

tcExecutionTableBody=tcExecutionTableBody+tcRowData

log.info "******Test Case: "+tcName+"="+tcStatus+"******"

}  // tc loop closing

} // suite loop closing

def endTimeTotal = Calendar.getInstance().getTime();
def totalExecTimeMs = getDateDiffInMilliseconds(startTimeTotal, endTimeTotal).toString()
log.info "Total Test Execution Time (Milliseconds): " + totalExecTimeMs

def totalExecTimeMsStr=calculateTotalExecDurationFromMillis(getDateDiffInMilliseconds(startTimeTotal, endTimeTotal));

def startTimeTotalStr=new SimpleDateFormat("dd-MMM-yyyy HH:mm:ss").format(startTimeTotal);
def endTimeTotalStr=new SimpleDateFormat("dd-MMM-yyyy HH:mm:ss").format(endTimeTotal);

// BUILD_STATUS
if(failedCount!=0) {
	buildStatus="FAIL";
	buildStatusClass="buildFail";
} else{
	buildStatus="PASS";
	buildStatusClass="buildPass";
}

project.setPropertyValue("BuildStatus",buildStatus)

def executionSummaryTable=null;

if(showBuildStatus.equalsIgnoreCase("true")){
	executionSummaryTable="<h3>Test Execution Summary: [Environment = "+environment+"]<span id='buildStatusSection'>[Build Status: <span class='"+buildStatusClass+"'>"+buildStatus+"</span>]</span></h3><table id='executionSummaryTable'><tr><th>Total Available Test Cases</th><th>Test Cases Run<th>Passed</th><th>Failed</th><th>Skipped</th><th>Pass (%)</th><th>Start DateTime</th><th>End DateTime</th><th>Total Duration (hh:mm:ss.SSS)</th></tr>"
} else{
	executionSummaryTable="<h3>Test Execution Summary: [Environment = "+environment+"]</h3><table id='executionSummaryTable'><tr><th>Total Available Test Cases</th><th>Test Cases Run<th>Passed</th><th>Failed</th><th>Skipped</th><th>Pass (%)</th><th>Start DateTime</th><th>End DateTime</th><th>Total Duration (hh:mm:ss.SSS)</th></tr>"
}





passPercentage=(float)passedCount/totalTCRunCount*100
executionSummaryTable=executionSummaryTable+"<tr><td>"+tcCount+"</td><td>"+totalTCRunCount+"</td><td>"+passedCount+"</td><td>"+failedCount+"</td><td>"+skippedTestCaseCount+"</td><td>"+String.format("%.02f", passPercentage)+"</td><td>"+startTimeTotalStr+"</td><td>"+endTimeTotalStr+"</td><td>"+totalExecTimeMsStr+"</td></tr></table></br>"
rawHtmlStr=rawHtmlStr+executionSummaryTable



// write skipped list data if there is any

if(skippedTestCaseCount!=0)
{
def skippedTCListData="===================Skipped Test Case List===================\n\n";
for(skippedTC in skippedTestCaseList){
	skippedTCListData=skippedTCListData+skippedTC+"\n"
}
def skippedTCListFileName="SkippedTestCaseList_"+tempStr+".txt"
def skippedTCListFilePath=htmlReportCurrentDirPath.toString()+"//"+skippedTCListFileName;
writeDataToFile(skippedTCListData,skippedTCListFilePath)
// adding hyperlink to skipped test cases list file
rawHtmlStr=rawHtmlStr+"<a href='./"+skippedTCListFileName+"'>Click here to view the skipped test cases list</a>"
}

def tcExecutionTable="<table id='tcExecutionTable'><tr><th>S.No.</th><th>Test Suite</th><th>TC Name</th><th>Step(s)</th><th>Status</th></tr>"
rawHtmlStr=rawHtmlStr+tcExecutionTable
rawHtmlStr=rawHtmlStr+tcExecutionTableBody+"</body></html>"


// write the html report at the end of execution   
def htmlFileAbsolutePath = htmlReportCurrentDirPath.toString()+"//"+htmlReportFileName;

writeDataToFile(rawHtmlStr,htmlFileAbsolutePath)

log.info "===============AUTOMATION EXECUTION FINISHED==============="
log.info "******EXECUTION SUMMARY******"
log.info "BUILD_STATUS: "+buildStatus
log.info "Total Available Test Cases="+tcCount+", Test Cases Run="+totalTCRunCount+", Passed="+passedCount+", Failed="+failedCount+", Pass (%)="+passPercentage+", Skipped Test Cases="+skippedTestCaseCount+", Skipped Test Suite Count="+skippedTestSuiteCount

log.info "Total Execution Time (Milliseconds)="+totalExecTimeMs

// writing execution script logs to file
def logFilePath=htmlReportCurrentDirPath.toString()+"//ExecutionLog_"+tempStr+".txt"
writeLogToFile(logFilePath)

log.info testRunner.metaClass.methods*.name.unique().sort()

